from django.contrib.auth.models import Userfrom django import formsclass SignUpForm(forms.ModelForm):    password = forms.CharField(label='Password', widget=forms.PasswordInput)    Response_password = forms.CharField(label='Res_password', widget = forms.PasswordInput)    class Meta:        model = User        fields = ['username', 'password', 'Response_password', 'first_name', 'last_name', 'email']    def clean_Response_password(self):        cd = self.cleaned_data        if cd['password'] != cd['Response_password']:            raise forms.ValidationError('비밀번호가 일치하지 않습니다.')        return cd['Response_password']